INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES 
('cdi','theme','Socio-economic risk factors','Risks determined by socioeconomic status, which is commonly conceptualized as the social standing or class of an individualor group. It is often measured as a combination of education, income and occupation.  ','FALSE','http://www.apa.org/topics/socioeconomic-status/');
INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES 
('cdi','theme','Housing','A housing unit is a house, apartment, group of rooms, or single room occupied or intended for occupancy as separate living quarters.','FALSE','https://www.census.gov/housing/ahs/files/Appendix%20A.pdf');
INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES 
('cdi','theme','Poverty','Condition where people''s basic needs for food, clothing, and shelter are not being met.','FALSE','http://www.businessdictionary.com/definition/poverty.html#ixzz3r6n5d6qZ');
