INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES 
('cdi','theme','Survival rates','The average probability of survival and reproduction of the organisms of each generation of a species (population). The survival rate is measured by the ratio of the number of adults that reproduce to the number born in each generation (or the number of eggs de-posited, spawn laid, seeds ripened, and so forth).','FALSE','http://encyclopedia2.thefreedictionary.com/Survival+Rate');
INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES 
('cdi','theme','Host Abundance','Abundance is the number of organisms in a population, combining “intensity“ (density within inhabited areas) and “prevalence“ (number and size of inhabited areas)','FALSE','http://biophysics.sbg.ac.at/glossary/eco-1.pdf');
INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES 
('cdi','theme','Pathogen Reproduction rate','The average number of persons infected by a single disease source; the number of expected secondary infections resulting from a single infectious case. This rate is affected by the duration of infectivity, infectiousness of the organism, and number of susceptible people with whom the infected person comes in contact.','FALSE','http://practice.sph.umich.edu/micphp/epicentral/basic_reproduc_rate.php');
INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES 
('cdi','theme','Zoonotic carriers','A zoonotic disease is a disease that can be passed between animals and humans. Zoonotic diseases can be caused by viruses, bacteria, parasites, and fungi. ','FALSE','http://www.cdc.gov/onehealth/zoonotic-diseases.html.');
INSERT INTO TERM (lexicon_identifier,context_identifier,term,description,is_root,url) VALUES 
('cdi','theme','Habitat availability','Habitat availability is the accessibility and procurability of physical and biological components of a habitat by animals. ','FALSE','http://www.webpages.uidaho.edu/range456/readings/krausman.pdf');
